int gcd ( ) ; int lcm ( ) ; int main ( ) { int n , c , d ; scanf ( "%d" , & n ) ; while ( n -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; c = gcd ( a , b ) ; d = lcm ( a , b ) ; printf ( "%d %d" , c , d ) ; } } int gcd ( int n1 , int n2 ) { int i , g ; if ( n2 == 0 ) { return n1 ; } else { for ( i = 0 ; ( i <= n1 ) || ( i <= n2 ) ; i ++ ) if ( n1 % i == 0 && n2 % i == 0 ) g = i ; return g ; } } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; }