int main ( ) { int T ; int A , B , lcm ; int numerator , denominator , remainder , gcd ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%d%d" , & A , & B ) ; lcm = A * B ; if ( A > B ) { numerator = A ; denominator = B ; } else { numerator = B ; denominator = A ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }