long long int Gcd ( int a , int b ) { if ( b == 0 ) return a ; else if ( a >= b ) Gcd ( b , a % b ) ; } int main ( ) { int t = 0 ; scanf ( "%d" , & t ) ; long long int a = 0 , b = 0 , temp = 0 ; long long int lcm = 1 ; long long int gcd = 0 ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; if ( b > a ) { temp = a ; a = b ; b = temp ; } gcd = Gcd ( a , b ) ; lcm = a * b ; lcm /= gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }