int gcd ( int a , int b ) { if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int n , a [ 1000 ] , b [ 1000 ] , i ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a [ i ] , & b [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "\n%d %d " , gcd ( a [ i ] , b [ i ] ) , lcm ( a [ i ] , b [ i ] ) ) ; } return 0 ; }