void main ( ) { int num1 , num2 , gcd , lcm , rem , numrtr , denomntr ; printf ( "entertwonum\n" ) ; scanf ( "%d %d" , & num1 , & num2 ) ; if ( num1 > num2 ) { numrtr = num1 ; denomntr = num2 ; } else { numrtr = num2 ; denomntr = num1 ; } rem = numrtr % denomntr ; while ( ( rem != 0 ) ) { numrtr = denomntr ; denomntr = rem ; rem = numrtr % denomntr ; } gcd = denomntr ; lcm = num1 * num2 / gcd ; printf ( "GCDis=%d\n" , gcd ) ; printf ( "LCMis=%d\n" , lcm ) ; }