long long int gcd ( long long int a , long long int b ) { if ( a == 0 || b == 0 ) { return 0 ; } if ( a == b ) { return b ; } if ( a > b ) { return gcd ( a - b , b ) ; } return gcd ( a , b - a ) ; } int main ( ) { long long int test ; scanf ( "%lld" , & test ) ; while ( test -- > 0 ) { long long int n , a ; scanf ( "%lld %lld" , & n , & a ) ; long long int gc = gcd ( n , a ) ; printf ( "%lld " , gc ) ; printf ( "%lld\n" , ( n * a ) / gc ) ; } }