int main ( ) { int a = 0 , b = 0 , r , max , a1 , b1 ; printf ( "enter two positive numbers:" ) ; scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; if ( a != 0 && b != 0 ) { a1 = a ; b1 = b ; if ( a > b ) { while ( r == 0 ) { r = a1 % b1 ; a1 = b1 ; b1 = r ; } printf ( "GCD of %d and %d is %d\n" , a , b , b1 ) ; } else { while ( r == 0 ) { r = b1 % a1 ; a1 = r ; b1 = a1 ; } printf ( "GCD of %d and %d is %d" , a , b , a1 ) ; } } max = ( a > b ) ? a : b ; while ( 1 ) { if ( max % a == 0 && max % b == 0 ) { printf ( "LCM of %d and %d is %d\n" , a , b , max ) ; break ; } ++ max ; } return 0 ; }