int main ( void ) { int t , i , n , s , j ; scanf ( "%d" , & t ) ; while ( t < 1 || t > 10 ) { scanf ( "%d" , & t ) ; } while ( t > 0 ) { s = 0 ; scanf ( "%d" , & n ) ; while ( n < 2 || n > 50000 ) { scanf ( "%d" , & n ) ; } int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; while ( a [ i ] < 1 || a [ i ] > 100000 ) { scanf ( "%d" , & a [ i ] ) ; } } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { s += a [ i + 1 ] ; } else { s += a [ i ] ; i ++ ; } } printf ( "%d\n" , s ) ; t -- ; } return 0 ; }