int main ( ) { int t , i , j ; long int a [ 50000 ] ; long int n , min = 100000 ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%ld" , & a [ j ] ) ; if ( a [ j ] < min ) min = a [ j ] ; } printf ( "%ld\n" , min ) ; } return 0 ; }