main ( ) { int test , i , a , b ; int gcd ( int a , int b ) ; int lcm [ test ] , hcf [ test ] ; scanf ( "%d" , & test ) ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; hcf [ i ] = gcd ( a , b ) ; if ( a % b == 0 ) { lcm [ i ] = a ; } else { lcm [ i ] = a * b / hcf [ i ] ; } } for ( i = 0 ; i < test ; i ++ ) printf ( "%d %d\n" , hcf [ i ] , lcm [ i ] ) ; } int gcd ( int x , int y ) { if ( x % y == 0 ) return y ; else return gcd ( y , x % y ) ; }