int main ( ) { int t ; int n ; scanf ( "%d\n" , & t ) ; int i , j , k , l ; int sum [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d\n" , & n ) ; sum [ i ] = 0 ; int nos [ n ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d " , & nos [ j ] ) ; } k = 0 ; l = k + 1 ; while ( k < n - 1 && l < n - 1 ) { if ( nos [ k ] < nos [ l ] ) { sum [ i ] += nos [ k ] ; l ++ ; } else { sum [ i ] += nos [ l ] ; k = l ; l ++ ; } } } for ( i = 0 ; i < t ; i ++ ) printf ( "%d\n" , sum [ i ] ) ; }