int main ( ) { int n , t , i , tree , k , temp , j ; int * house ; scanf ( "%d" , & t ) ; while ( t ) { scanf ( "%d" , & n ) ; house = ( int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & house [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) for ( j = i + 1 ; j < n ; j ++ ) { if ( house [ j ] < house [ i ] ) { temp = house [ j ] ; house [ j ] = house [ i ] ; house [ i ] = temp ; } } tree = house [ 0 ] * ( n - 1 ) ; printf ( "%d\n" , tree ) ; t -- ; free ( house ) ; } return 0 ; }