int main ( ) { int a , b , t , i , j , g , l ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a == 1 || b == 1 ) { g = 1 ; l = ( a * b ) / g ; } else { if ( a <= b ) { for ( j = a ; j >= 2 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { g = j ; break ; } else g = 1 ; } } else { for ( j = b ; j >= 2 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { g = j ; break ; } else g = 1 ; } } } l = ( a * b ) / g ; printf ( "%d %d\n" , g , l ) ; } }