int GCD ( long long int a , long long int b ) { if ( b == 0 ) return a ; return GCD ( b , a % b ) ; } int LCM ( long long int a , long long int b ) { return b * a / GCD ( a , b ) ; } int main ( ) { long long int a , b , t ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; printf ( "%lld " , GCD ( a , b ) ) ; printf ( "%lld " , LCM ( a , b ) ) ; } return 0 ; }