int main ( ) { long int a , b , hcf [ 1000 ] , i , j , gcd [ 1000 ] , small ; int flag , t ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { flag = 1 ; scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; small = a ; if ( b < a ) small = b ; for ( j = 1 ; j <= small ; j ++ ) { if ( a % j != 0 || b % j != 0 ) flag = 0 ; if ( flag == 1 ) gcd [ i ] = j ; } hcf [ i ] = a * b / gcd [ i ] ; } for ( i = 0 ; i < t ; i ++ ) { printf ( "\n" ) ; printf ( "%li" , gcd [ i ] ) ; printf ( " %li" , hcf [ i ] ) ; } return 0 ; }