int main ( ) { int t , n1 , n2 , i , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { gcd = 1 ; lcm = 1 ; scanf ( "%d %d" , & n1 , & n2 ) ; int n ; n = n1 <= n2 ? n1 : n2 ; for ( i = 2 ; i <= n ; i ++ ) if ( ( n1 % i == 0 ) && ( n2 % i == 0 ) ) gcd = i ; lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }