int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , max = 0 ; scanf ( "%d" , & n ) ; int a [ n ] , b [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; if ( a [ i ] >= max ) max = a [ i ] ; } int newmax = - 100000 ; for ( i = 0 ; i < n ; i ++ ) { b [ i ] = a [ i ] - max ; if ( b [ i ] >= newmax && b [ i ] != 0 ) newmax = b [ i ] ; } printf ( "%d\n" , max + newmax ) ; } return 0 ; }