void main ( ) { int test , num1 , num2 , minmultiple , lcm , gcd , i = 0 ; scanf ( "%d" , & test ) ; while ( test -- ) { scanf ( "%d %d" , & num1 , & num2 ) ; minmultiple = ( num1 > num2 ) ? num1 : num2 ; while ( 1 ) { if ( minmultiple % num1 == 0 && minmultiple % num2 == 0 ) { lcm = minmultiple ; break ; } ++ minmultiple ; } for ( i = 1 ; i <= num1 && i <= num2 ; ++ i ) { if ( num1 % i == 0 && num2 % i == 0 ) gcd = i ; } printf ( "%d %d" , gcd , lcm ) ; } }