int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n ; scanf ( "%lld" , & n ) ; long long int a [ n ] , i , s = 0 , c = 1 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; if ( ( a [ i ] > a [ i - 1 ] ) && ( i != 0 ) ) { c ++ ; } else { s = s + ( c * ( c - 1 ) ) / 2 ; c = 1 ; } } s = s + ( c * ( c - 1 ) ) / 2 ; printf ( "%lld\n" , s + n ) ; } }