int main ( ) { int a , b , lcm , gcd , remainder , numerator , denominator ; printf ( "Enter two numbers:" ) ; scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { numerator = a ; denominator = b ; } else { numerator = b ; denominator = a ; } while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = a * b / gcd ; printf ( "GCD of %d and %d is: %d\n" , a , b , gcd ) ; printf ( "LCM of %d and %d is: %d\n" , a , b , lcm ) ; }