void main ( ) { int n , sum = 0 , gr = 0 , i , j , num , n1 , t ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { gr = 0 ; scanf ( "%d" , & num ) ; n1 = num ; if ( n1 > gr ) gr = num ; printf ( "Greater = %d\n" , gr ) ; sum = sum + num ; } sum = sum - gr ; printf ( "Cost of operation = %d \n" , sum ) ; printf ( "%d" , sum ) ; sum = 0 ; } }