main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; int i ; int count = 0 ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } int j ; for ( i = 0 ; i < n ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ j ] > a [ j - 1 ] ) count ++ ; else if ( a [ j ] < a [ j - 1 ] ) break ; } } int lastcount ; lastcount = count + n ; printf ( "%d\n" , lastcount ) ; } }