int find ( int a , int b ) ; int main ( ) { int t , a , b , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) gcd = find ( a , b ) ; else gcd = find ( b , a ) ; lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } } int find ( int a , int b ) { while ( a != b ) { if ( a > b ) return find ( a - b , b ) ; else return find ( a , b - a ) ; } return a ; }