int main ( ) { int i , num1 , num2 , gcd , lcm , remainder , numerator , denominator ; int t ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d %d" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = num1 % num2 ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = num1 * num2 / gcd ; printf ( " %d %d " , gcd , lcm ) ; lcm = 0 ; gcd = 0 , remainder = 0 ; } return 0 ; }