int main ( ) { int t , i , n , num , k , j , temp , large ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int arr1 [ n ] ; int arr2 [ n ] ; for ( j = 0 ; j < n ; j ++ ) { arr1 [ j ] = 0 ; } for ( j = 0 ; j < n ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & arr2 [ k ] ) ; if ( k >= 1 ) { if ( arr1 [ k ] > arr1 [ k - 1 ] ) { arr2 [ k ] = arr2 [ k ] + arr1 [ k ] ; } else arr2 [ k ] = arr2 [ k ] + arr1 [ k - 1 ] ; } else arr2 [ k ] = arr2 [ k ] + arr1 [ k ] ; } for ( k = 0 ; k <= j ; k ++ ) { temp = arr1 [ k ] ; arr1 [ k ] = arr2 [ k ] ; arr2 [ k ] = temp ; } } large = arr1 [ 0 ] ; for ( k = 1 ; k < n ; k ++ ) { if ( arr1 [ k ] > large ) large = arr1 [ k ] ; } printf ( "%d\n" , large ) ; } return 0 ; }