int main ( ) { int i , t , x , n , p , u ; long sum ; int a [ 50002 ] ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( p = 0 ; p < n ; p ++ ) scanf ( "%d" , & a [ p ] ) ; int min = a [ 0 ] ; for ( x = 0 ; x < n ; x ++ ) { if ( a [ x ] < min ) min = a [ x ] ; } for ( u = 0 ; u < p - 1 ; u ++ ) { sum = sum + min ; } printf ( "%ld\n" , sum ) ; } return 0 ; }