int main ( ) { long n1 , n2 , t , i , r , l , p ; scanf ( "%ld" , & t ) ; long gcd [ t ] , lcm [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & n1 , & n2 ) ; if ( n1 < n2 ) { l = n1 ; n1 = n2 ; n2 = l ; } p = n1 * n2 ; while ( n2 != 0 ) { r = n1 % n2 ; n1 = n2 ; n2 = r ; } gcd [ i ] = n1 ; lcm [ i ] = p / gcd [ i ] ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%ld %ld\n" , gcd [ i ] , lcm [ i ] ) ; return 0 ; }