int main ( ) { long int j , a , b , max , min ; int i , n ; long long int p ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%ld %ld" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } for ( j = min ; j > 0 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { printf ( "%ld " , j ) ; j = 0 ; } } for ( p = max ; p <= ( a * b ) ; p = p + max ) { if ( p % a == 0 && p % b == 0 ) { printf ( "%I64d\n" , p ) ; break ; } } } }