int main ( ) { int t , i ; int long a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; int z = 0 ; if ( a > b ) { if ( a % b == 0 ) { printf ( "%d " , b ) ; printf ( "%d\n" , a ) ; } else if ( a % b != 0 ) { for ( i = 1 ; i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { if ( i > z ) { z = i ; } } } printf ( "%d " , z ) ; printf ( "%d\n" , a * b / z ) ; } } if ( b > a ) { if ( b % a == 0 ) { printf ( "%d " , a ) ; printf ( "%d\n" , b ) ; } if ( b % a != 0 ) { for ( i = 1 ; i <= a ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { if ( i > z ) { z = i ; } } } printf ( "%d " , z ) ; printf ( "%d\n" , a * b / z ) ; } } } }