typedef unsigned long long ull ; typedef long long ll ; ll ReadInt ( void ) { int ch , neg = 0 ; ll val = 0 ; while ( ( ch = getchar ( ) ) < '0' || ch > '9' ) { if ( ch == '-' ) { neg = 1 ; ch = getchar ( ) ; break ; } } while ( ch >= '0' && ch <= '9' ) { val *= 10 ; val += ch - '0' ; ch = getchar ( ) ; } if ( neg ) val = - val ; return val ; } ull CalcGCD ( ull a , ull b ) { ull temp ; if ( a < b ) { temp = a ; a = b ; b = temp ; } while ( b != 0 ) { temp = b ; b = a % b ; a = temp ; } return a ; } int main ( void ) { ull numtests , a , b , gcd ; numtests = ReadInt ( ) ; while ( numtests -- > 0 ) { a = ReadInt ( ) ; b = ReadInt ( ) ; gcd = CalcGCD ( a , b ) ; printf ( "%Lu %Lu\n" , gcd , ( a / gcd ) * b ) ; } return 0 ; }