int main ( ) { int t = 0 ; long int a , b , gcd = 1 , lcm , p ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; if ( b < a ) { p = b ; while ( p != 0 ) { if ( ( a % p == 0 ) && ( b % p == 0 ) ) { gcd = p ; break ; } p -- ; } } else { p = a ; while ( p != 0 ) { if ( ( a % p == 0 ) && ( b % p == 0 ) ) { gcd = p ; break ; } p -- ; } } lcm = ( a * b ) / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return ; }