int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a [ MAX ] , i , j , n , cost = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; while ( n != 1 ) { i = 0 ; if ( a [ i ] < a [ i + 1 ] ) { for ( j = i + 1 ; j < n ; j ++ ) a [ j ] = a [ j + 1 ] ; n -- ; cost += a [ 0 ] ; } else { for ( j = i ; j < n ; j ++ ) a [ j ] = a [ j + 1 ] ; n -- ; cost += a [ 0 ] ; } } printf ( "%d\n" , cost ) ; } return 0 ; }