int gcd ( int a , int b ) { int t ; while ( b != 0 ) { t = a % b ; a = b ; b = t ; } printf ( "%d\t" , a ) ; return 0 ; } int lcm ( int a , int b ) { int max = a ; while ( 1 ) { if ( ( max % a == 0 ) && ( max % b == 0 ) ) { printf ( "%d\n" , max ) ; break ; } else max ++ ; } return 0 ; } int main ( ) { int b , i , a , n , temp ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( b > a ) { temp = a ; a = b ; b = temp ; } gcd ( a , b ) ; lcm ( a , b ) ; } return 0 ; }