int main ( void ) { int t , n ; scanf ( "%d" , & t ) ; while ( t -- ) { int i , min = 0 , cost , n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; min = a [ 0 ] ; if ( a [ 1 ] < min ) min = a [ 1 ] ; cost = min ; for ( i = 2 ; i < n ; i ++ ) { if ( min < a [ i ] ) { cost = cost + min ; min = a [ i ] ; } else { min = a [ i ] ; cost = cost + min ; } } printf ( "%d\n" , cost ) ; } return 0 ; }