int main ( void ) { int cases , row , * * data ; int i , j , k , max ; int up , up_left ; scanf ( "%d" , & cases ) ; for ( i = 0 ; i < cases ; i ++ ) { scanf ( "%d" , & row ) ; data = ( int * * ) malloc ( row * sizeof ( int * ) ) ; for ( j = 0 ; j < row ; j ++ ) { data [ j ] = ( int * ) malloc ( row * sizeof ( int ) ) ; for ( k = 0 ; k < row ; k ++ ) data [ j ] [ k ] = 0 ; } j = 0 ; k = 0 ; max = 0 ; while ( j < row ) { scanf ( "%d" , & data [ j ] [ k ] ) ; if ( j > 0 ) { up = data [ j - 1 ] [ k ] ; up_left = 0 ; if ( k > 0 ) up_left = data [ j - 1 ] [ k - 1 ] ; if ( up_left > up ) data [ j ] [ k ] += up_left ; else data [ j ] [ k ] += up ; if ( j == row - 1 ) { if ( data [ j ] [ k ] > max ) max = data [ j ] [ k ] ; } } k ++ ; if ( k == j + 1 ) { j ++ ; k = 0 ; } } printf ( "%d\n" , max ) ; } return 0 ; }