int GCD ( int x , int y ) { if ( x < y ) { if ( y % x == 0 ) return x ; else return ( GCD ( x , y % x ) ) ; } else { if ( x % y == 0 ) return y ; else return ( GCD ( x % y , y ) ) ; } } int main ( ) { int t , i ; int n1 , n2 , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & n1 , & n2 ) ; gcd = GCD ( n1 , n2 ) ; lcm = ( n1 * n2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }