int main ( ) { int t , i ; long long a , b , gcd = 1 , lcm , j , l , s , p ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; if ( a >= b ) { l = a ; s = b ; } else { l = b ; s = a ; } for ( j = 1 ; j <= s ; j ++ ) { p = s / j ; if ( l % p == 0 && p * j == s ) { gcd = p ; break ; } } lcm = ( a * b ) / gcd ; printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }