int main ( ) { long long int T , num , ans , scratch , prev , change ; scanf ( "%lld" , & T ) ; while ( T -- ) { prev = - 1 ; change = 0 ; scanf ( "%lld" , & num ) ; ans = num ; while ( num -- ) { scanf ( "%lld" , & scratch ) ; if ( scratch < prev && change ) { change = 0 ; ans ++ ; } else { change ++ ; } prev = scratch ; } if ( change ) ans ++ ; printf ( "%d\n" , ans ) ; } return 0 ; }