int main ( ) { int t ; scanf ( "%d" , & t ) ; int a , b ; for ( int i = 0 ; i < t ; i ++ ) { for ( int i = 0 ; i < 2 ; i ++ ) { scanf ( "%d %d\n" , & a , & b ) ; } } gcd ( a , b ) ; lcm ( a , b ) ; return 0 ; } int gcd ( int a , int b ) { if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; }