int main ( ) { int i , j , c = 0 , n , d , T , check , flag ; scanf ( "%d" , & T ) ; while ( T -- ) { d = 0 , flag = 1 ; ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } check = arr [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( arr [ i ] == check ) continue ; else { flag = 0 ; break ; } } if ( flag == 0 ) { for ( j = 0 ; j < n ; j ++ ) { for ( i = j ; i < n - 1 ; i ++ ) { if ( arr [ i + 1 ] >= arr [ i ] ) c += 1 ; else break ; } d += ( c * ( c + 1 ) ) / 2 ; c = 0 ; j = i ; } printf ( "%d\n" , n + d ) ; } else printf ( "%d\n" , n ) ; } return 0 ; }