int main ( ) { long long int t , n , i , j ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld" , & n ) ; long long int a [ n ] , b [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%lld" , & a [ j ] ) ; b [ 0 ] = 1 ; for ( j = 1 ; j < n ; j ++ ) { if ( a [ j - 1 ] <= a [ j ] ) b [ j ] = b [ j - 1 ] + 2 ; else b [ j ] = b [ j - 1 ] + 1 ; } printf ( "%lld\n" , b [ n - 1 ] ) ; } return 0 ; }