void show ( int a [ ] , int n ) { int i ; for ( i = 0 ; i < n ; i ++ ) { printf ( "@%d " , a [ i ] ) ; } printf ( "\n" ) ; } int main ( ) { int T , N , i , j , s = 0 ; int a [ 50 ] ; scanf ( "%d" , & T ) ; while ( T -- ) { scanf ( "%d" , & N ) ; for ( i = 0 ; i < N ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < N - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { for ( j = i + 1 ; j < N ; j ++ ) a [ j - 1 ] = a [ j ] ; s = s + a [ i ] ; N -- ; i -- ; } else if ( a [ i ] < a [ i + 1 ] ) { for ( j = i + 2 ; j < N ; j ++ ) a [ j - 1 ] = a [ j ] ; s = s + a [ i ] ; N -- ; i -- ; } else { continue ; } } printf ( "%d\n" , s ) ; } return 0 ; }