int main ( void ) { long long int t , i , n , a [ 50000 ] , j , cnt , min ; scanf ( "%lld" , & t ) ; while ( t > 0 ) { scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lid " , & a [ i ] ) ; min = a [ 0 ] ; for ( i = 0 , j = i + 1 ; i < n ; i ++ ) { if ( a [ j ] < a [ i ] && a [ i ] != 0 && a [ j ] != 0 ) { min = a [ j ] ; a [ i ] = 0 ; } else a [ j ] = 0 ; } for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] == 0 ) cnt ++ ; } printf ( "%lld\n" , min * cnt ) ; t -- ; } return 0 ; }