int main ( ) { int t , n , j , i , swap ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n ] , sum = 0 ; for ( i = 1 ; i <= n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 1 ; i <= n ; i ++ ) { for ( j = 1 ; j <= n ; j ++ ) { if ( a [ j ] > a [ j + 1 ] ) { swap = a [ j ] ; a [ j ] = a [ j + 1 ] ; a [ j + 1 ] = swap ; } } } for ( i = 1 ; i <= ( n - 1 ) ; i ++ ) sum = sum + a [ 1 ] ; printf ( "%d\n" , sum ) ; } return 0 ; }