int greatestCommonInteger ( int a , int b ) { int temp ; while ( b != 0 ) { temp = b ; b = a % b ; a = temp ; } return a ; } int main ( void ) { int a , b ; int n ; int lcm , gcd ; scanf ( "%d" , & n ) ; int i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; gcd = greatestCommonInteger ( a , b ) ; lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }