int main ( ) { int n , i ; long long int a , b , numerator , denominator , gcd , lcm , rem ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld %lld" , & a , & b ) ; if ( a > b ) { numerator = a ; denominator = b ; } else if ( b > a ) { numerator = b ; denominator = a ; } rem = numerator % denominator ; while ( rem != 0 ) { numerator = denominator ; denominator = rem ; rem = numerator % denominator ; } gcd = denominator ; lcm = ( a * b ) / gcd ; printf ( "%lld " , gcd ) ; printf ( "%lld\n" , lcm ) ; } }