int main ( ) { long int t , a , i , j , temp ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld" , & a ) ; long int ar [ a ] ; for ( i = 0 ; i < a ; i ++ ) scanf ( "%ld" , & ar [ i ] ) ; for ( i = 0 ; i < a - 1 ; i ++ ) { for ( j = i + 1 ; j < a ; j ++ ) { if ( ar [ i ] > ar [ j ] ) { temp = ar [ i ] ; ar [ i ] = ar [ j ] ; ar [ j ] = temp ; } } } printf ( "%ld\n" , ar [ 0 ] * ( a - 1 ) ) ; } }