int gcd ( int a , int b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int main ( ) { int t ; int a , b , min , max , hcf , lcm ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } hcf = gcd ( max , min ) ; lcm = ( a * b ) / hcf ; printf ( "%d%d" , hcf , lcm ) ; } return 0 ; }