int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] , dp [ n ] ; int i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; dp [ i ] = 1 ; } for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] > a [ i - 1 ] ) dp [ i ] = dp [ i - 1 ] + 1 ; } int sum = 0 ; for ( i = 0 ; i < n ; i ++ ) sum = sum + dp [ i ] ; printf ( "%d\n" , sum ) ; } }