int main ( ) { int t , n , i , j , k , temp ; scanf ( "%d" , & t ) ; int cost [ t ] ; for ( k = 0 ; k < t ; k ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { if ( a [ i ] > a [ j ] ) { temp = a [ i ] ; a [ i ] = a [ j ] ; a [ j ] = temp ; } } } cost [ k ] = a [ 0 ] * ( n - 1 ) ; } for ( k = 0 ; k < t ; k ++ ) printf ( "%d" , cost [ k ] ) ; return 0 ; }